mapper.update:
UPDATE transaction
dlvy_date = #{divyDate:DATE} , dlvy_request = #{divyRequest:VARCHAR} , payment_option = #{paymentOption:CHAR} , receiver_name = #{receiverName:VARCHAR} , receiver_phone = #{receiverPhone:VARCHAR} , demailaddr = #{divyAddr:VARCHAR}
WHERE tran_no = #{tranNo:NUMERIC}

mapper.select:
  	SELECT COUNT(*)
  			FROM(	SELECT t.tran_no , p.prod_no 
						FROM product p, transaction t, users u	
						WHERE t.prod_no = p.prod_no 
						AND t.buyer_id = u.user_id 
						AND u.user_id = #{buyerId}
				
						
						
					 			AND t.tran_no = #{search.searchKeyword}
							
							
					 			AND p.prod_no = #{search.searchKeyword}
							
						
					 ) countTable

  	SELECT COUNT(*)
  			FROM(	SELECT t.tran_no , p.prod_no 
						FROM product p, transaction t
						WHERE t.prod_no = p.prod_no 
						
				
						
						
					 			AND t.tran_no = #{search.searchKeyword}
							
							
					 			AND p.prod_no = #{search.searchKeyword}
							
						
					 ) countTable
