mapper.update:
   	UPDATE users
   	
   		user_name 	= #{userName} ,
		cell_phone	= #{phone},
		addr 		= #{addr} ,
		email		= #{email}
   	
   	WHERE user_id = #{userId}

mapper.select:
  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  			FROM (	
  						
  					) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

mapper.sql:
 
